import React from 'react';
import { formatPrice } from '../helpers';

class EditFishForm extends React.Component {
    handlechange = (event) => {
    console.log(event.currentTarget.value);
    const updatedFish = {...this.props.fish}
    }
    render() {
        return (
            <div className="fish-edit">
                <input type="text" name="name" onChange={this.handlechange} value={this.props.fish.name} />
                <input type="text" name="price" onChange={this.handlechange} value={formatPrice(this.props.fish.price)} />
                <select name="status" onChange={this.handlechange} value={this.props.fish.status}>
                    <option value="available">Fresh!</option>
                    <option value="unavailable">Sold Out!</option>
                </select>
                <textarea name="desc" onChange={this.handlechange} value={this.props.fish.desc}></textarea>
                <input type="text" name="image" onChange={this.handlechange} value={this.props.fish.image} />
            </div>
        )
    }
}

export default EditFishForm;
